import React, { useState, useEffect } from "react";
import "./Card.css";
//import { connect } from "react-redux";
//import { fetchImg } from "../../store/actions/photo";

function Card() {
  const [url, setUrl] = useState(
    "https://www.flickr.com/services/rest/?method=flickr.photos.getPopular&api_key=80d0cf5bb3998d2e51cc12f5d707fede&user_id=185307336%40N02&format=json&nojsoncallback=1"
  );

  const [src, setSrc] = useState([]);

  useEffect(() => {
    let urls = [];
    fetch(url)
      .then((response) => response.json())
      .then((data) => createImage(data));
    function createImage(data) {
      data.photos.photo.forEach((item) => {
        let serverId = item.server;
        let id = item.id;
        let secret = item.secret;
        let imgSrc =
          "https://live.staticflickr.com/" +
          serverId +
          "/" +
          id +
          "_" +
          secret +
          "_w.jpg";
        urls.push(imgSrc);
      });
    }
    setSrc([...src, urls]);
  }, []);

  //console.log(src);

  // useEffect(() => {
  //   this.props.fetchImg();
  // });

  return (
    <div className="Card" id="main">
      {src.map((item, index) => (
        <img key={item} src={item} alt="index" />
      ))}
    </div>
  );
}
export default Card;

