import React, { useState, useRef } from "react";
import classes from "./Card.css";

function Card() {
  const [url, setUrl] = useState(
    "https://www.flickr.com/services/rest/?method=flickr.photos.getPopular&api_key=80d0cf5bb3998d2e51cc12f5d707fede&user_id=185307336%40N02&format=json&nojsoncallback=1"
  );

  let [photo, setPhoto] = useState(null);

  let div = document.getElementById("main");

  fetch(url)
    .then((response) => response.json())
    .then((data) => createImage(data));
  //.finally((data) => console.log(1));

  function createImage(data) {
    //console.log(data);
    data.photos.photo.map((item) => {
      const farmId = item.farm;
      const serverId = item.server;
      const id = item.id;
      const secret = item.secret;

      //https://live.staticflickr.com/{server-id}/{id}_{secret}_{size-suffix}.jpg
      const img = document.createElement("img");

      div.append(
        '<img src="https://farm' +
          farmId +
          ".staticflickr.com/" +
          serverId +
          "/" +
          id +
          "_" +
          secret +
          '.jpg"/>'
      );
    });
  }

  return <div className={classes.Card} id="main"></div>;
}

export default Card;
