import React, { useState, useEffect } from "react";
import classes from "./Card.css";
//import { connect } from "react-redux";
//import { fetchImg } from "../../store/actions/photo";

function Card() {
  const [url, setUrl] = useState(
    "https://www.flickr.com/services/rest/?method=flickr.photos.getPopular&api_key=80d0cf5bb3998d2e51cc12f5d707fede&user_id=185307336%40N02&format=json&nojsoncallback=1"
  );

  const [imgSrcs, setImgSrcs] = useState([]);

  let src;
  //const [src, setSrc] = useState(null);

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => createImage(data));
  });

  function createImage(data) {
    data.photos.photo.map((item) => {
      let serverId = item.server;
      let id = item.id;
      let secret = item.secret;
      src =
        "https://live.staticflickr.com/" +
        serverId +
        "/" +
        id +
        "_" +
        secret +
        "_w.jpg";

      console.log(src);
      setImgSrcs([src]);
    });
  }

  // useEffect(() => {
  //   this.props.fetchImg();
  // });

  return <div className={classes.Card} id="main"></div>;
}
export default Card;

