import React, { useState, useRef } from "react";
import classes from "./Card.css";

function Card() {
  const [url, setUrl] = useState(
    "https://www.flickr.com/services/rest/?method=flickr.photos.getPopular&api_key=80d0cf5bb3998d2e51cc12f5d707fede&user_id=185307336%40N02&format=json&nojsoncallback=1"
  );
  const [photo, setPhoto] = useState([]);
  const el = useRef(null);

  fetch(url)
    .then((response) => response.json())
    .then((data) => photo.push(data))
    .finally(() => createImage());

  function createImage() {
    photo.map((item) => {
      console.log(item);
      const farmId = item.farm;
      const serverId = item.server;
      const id = item.id;
      const secret = item.secret;
      //https://live.staticflickr.com/{server-id}/{id}_{secret}_{size-suffix}.jpg

      const img = document.createElement("img");
      img.src =
        "https://live.staticflickr.com/" +
        serverId +
        id +
        secret +
        "q" +
        ".jpg";
      el.append(img);
    });
  }

  return <div className={classes.Card} ref={el}></div>;
}

export default Card;
